/**
 * Hook that detects clicks outside specified elements and invokes a callback.
 */
export declare function useClickAway<El extends Element>({ refs, onClickAway, isDisabled, }: {
    /**
     * Array of refs to elements. A click outside of any of these elements
     * will trigger the onClickAway callback.
     */
    refs: Array<React.RefObject<El>>;
    /**
     * Callback function that is executed when a click occurs outside the
     * specified elements.
     */
    onClickAway: (event: Event) => void;
    /**
     * Whether the click away functionality is temporary disabled
     */
    isDisabled?: boolean;
}): void;
