'use strict';

const getTagStyles = ({
  hasAdornmentStart,
  hasAdornmentEnd
}) => ({
  root: `
		${getRootAdornmentStyles({
    hasAdornmentStart,
    hasAdornmentEnd
  })}
		bg-neutral-secondary
		gap-1
		inline-flex
		items-center
		outline-none
		rounded-full
		transition-colors
	`,
  label: `text-body-12`
});
const getTagButtonStyles = () => ({
  button: `
			bg-transparent
			border-none
			focus-visible:focus-ring
			group
			h-4
			hover:cursor-pointer
			outline-none
			p-0
			rounded-6px
			w-4
		`,
  icon: `
			fill-tertiary
			group-active:fill-primary
			group-hover:fill-secondary
		`
});
function getRootAdornmentStyles({
  hasAdornmentStart,
  hasAdornmentEnd
}) {
  if (hasAdornmentStart) {
    return `
			ps-1
			pe-2
			h-6
		`;
  }
  if (hasAdornmentEnd) {
    return `
			ps-2
			pe-1
			h-6
		`;
  }
  return `
		px-2
		h-6
	`;
}

exports.getTagButtonStyles = getTagButtonStyles;
exports.getTagStyles = getTagStyles;
