/**
 * Validates that all children elements are of allowed component types
 * @param children - Array of React elements to validate
 * @param allowedTypes - Array of valid React component types
 * @throws {Error} Throws an error if any child component is not of an allowed type
 */
function validateChildren(children, allowedTypes) {
  children.forEach(child => {
    if (!allowedTypes.some(type => child.type === type)) {
      throw new Error('Invalid child component passed');
    }
  });
}

export { validateChildren as v };
